import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";

function Board() {
  const { subject } = useParams(); // URL에서 과목 이름 파라미터 가져오기
  const [posts, setPosts] = useState([]);
  const [title, setTitle] = useState("");
  const [content, setContent] = useState("");
  const [file, setFile] = useState(null);

  useEffect(() => {
    fetchPosts();
  }, [subject]);

  const fetchPosts = async () => {
    try {
      const response = await axios.get(`http://localhost:8080/posts/${subject}`);
      setPosts(response.data);
    } catch (error) {
      console.error("Error fetching posts:", error);
    }
  };

  const handleUpload = async (e) => {
    e.preventDefault();
    const formData = new FormData();
    formData.append("title", title);
    formData.append("content", content);
    formData.append("file", file);

    try {
      await axios.post(`http://localhost:8080/posts/${subject}`, formData);
      alert("게시글 업로드 성공");
      setTitle("");
      setContent("");
      setFile(null);
      fetchPosts(); // 업로드 후 게시글 목록 갱신
    } catch (error) {
      console.error("Error uploading post:", error);
    }
  };

  return (
    <div>
      <h1>{subject.replace(/-/g, " ")} 게시판</h1> {/* URL-friendly 과목 이름 변환 */}
      {/* 게시글 업로드 폼 */}
      <form onSubmit={handleUpload}>
        <div>
          <label>제목</label>
          <input
            type="text"
            value={title}
            onChange={(e) => setTitle(e.target.value)}
            placeholder="제목을 입력하세요"
            required
          />
        </div>
        <div>
          <label>내용</label>
          <textarea
            value={content}
            onChange={(e) => setContent(e.target.value)}
            placeholder="내용을 입력하세요"
            required
          />
        </div>
        <div>
          <label>파일</label>
          <input
            type="file"
            onChange={(e) => setFile(e.target.files[0])}
            required
          />
        </div>
        <button type="submit">게시글 업로드</button>
      </form>

      {/* 게시글 목록 */}
      <ul>
        {posts.map((post) => (
          <li key={post.id}>
            <h3>{post.title}</h3>
            <p>{post.content}</p>
            <a href={`http://localhost:8080/files/${post.filePath}`} download>
              파일 다운로드
            </a>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default Board;
